def.body:
{% if true %}
        case self
        {% for type in @type.constants %}
        when {{ type }} then Response::{{ type }}
        {% end %}
        else
          # Will never happen, but required to stop nil for sneaking into the
          # returned type union.
          raise ""
        end
      {% end %}

params = HTTP::Params {"name" => name}
body = {name: name, description: description, details: details, parent_id: id}
put("#{base}/#{id}?#{params}", body: body, as: API::Models::Metadata)
